const pallet = '0123456789ABCDEF'

function colorget(){
    const color_array = [
    String(Math.floor(Math.random() * 16)),
    String(Math.floor(Math.random() * 16)),
    String(Math.floor(Math.random() * 16)),
    String(Math.floor(Math.random() * 16)),
    String(Math.floor(Math.random() * 16)),
    String(Math.floor(Math.random() * 16))
    ]

    let color = "#"

    color_array.forEach(function(item) {
        color += pallet[item]
        item = null
    });

    return color;
}


