function generateCookie(){
    let user_cookie = "";

    const cookie_length = 20;
    const cookie_keyword = "abcdefghijklmnopqrstuvwxyz1234567890";

    for (let index = 0; index < cookie_length; index++) {
        let keyword_index = Math.floor(Math.random() * cookie_keyword.length)
        let element = cookie_keyword[keyword_index];

        user_cookie += element
    }

    return user_cookie
}

